schema.snippet:
{
  "$id": "https://opg.service.justice.gov.uk/opg.poas.makeregister/application-updated.json",
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "opg.poas.makeregister/application-updated",
  "type": "object",
  "properties": {
    "uid": {
      "type": "string",
      "description": "The UID of the LPA",
      "pattern": "M-([A-Z0-9]{4}){3}"
    },
    "source": {
      "type": "string",
      "description": "The source of the LPA application",
      "enum": ["APPLICANT", "PHONE"]
    },
    "type": {
      "type": "string",
      "description": "The type of LPA being applied for",
      "enum": ["hw", "pfa"]
    },
    "createdAt": {
      "type": "string",
      "description": "When the LPA was created",
      "format": "date-time"
    },
    "donor": {
      "type": "object",
      "properties": {
        "firstNames": {
          "type": "string",
          "description": "The first name(s) of the donor, including any middle names"
        },
        "lastName": {
          "type": "string",
          "description": "The last name of the donor"
        },
        "dob": {
          "type": "string",
          "description": "The date of birth of the donor",
          "format": "date"
        },
        "postcode": {
          "type": "string",
          "description": "The postcode of the donor's address",
          "pattern": "[A-Z0-9 ]{,9}"
        }
      },
      "required": ["firstNames", "lastName", "dob", "postcode"]
    },
    "required": ["uid", "source", "type", "createdAt", "donor"]
  }
}
